import ReactDOM from 'react-dom';
import { PerformanceTable } from 'choerodon-ui/pro';

class AffixHorizontalScrollbarTable extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      data: fakeData,
    };
  }

