import React, {
  useCallback,
  useMemo,
  cloneElement,
  createContext,
  useContext,
  useRef,
  useEffect,
  useState,
} from 'react';
import ReactDOM from 'react-dom';
import {
  PerformanceTable,
  Button,
  CheckBox,
  DataSet,
  Output,
  Pagination,
} from 'choerodon-ui/pro';
import { observer } from 'mobx-react-lite';
import { getEditorByField } from 'choerodon-ui/pro/lib/table/utils';

const Store = createContext();

export const StoreProvider = props => {
  const { children } = props;

  const userDs = useMemo(() => new DataSet({
    primaryKey: 'userid',
    name: 'large-user',
    queryUrl: 'https://www.fastmock.site/mock/423302b318dd24f1712751d9bfc1cbbc/mock/mock-user',
    dataKey: 'rows',
    autoQuery: true,
    pageSize: 10,
    fields: [
      {
        name: 'userid',
        type: 'string',
        label: '编号',
        required: true,
        unique: true, // 唯一索引或联合唯一索引组名 设置可编辑只有新增才能编辑,确保该字段或字段组唯一性
        help: '主键，区分用户',
      },
      {
        name: 'name',
        type: 'intl',
        label: '姓名',
      },
      {
        name: 'age',
