// Table.jsx
import React from 'react';
import TableDS from './DataSet';
import { Table, Icon } from 'choerodon-ui/pro';

const { Column } = Table;

class App extends React.Component {
	constructor() {
		this.tableDataSet = new DataSet(TableDS);
	}	

    function typeRenderer({ value, text, name, record, dataSet }) {
		const typeIcon = record.get('issueTypeVO_icon');
		return (
			<div>
				<Icon name={typeIcon} />
				<span>{value}</span>
			</div>
		)
	}

	function priorityBackgroundRenderer({ value, text, name, record, dataSet }) {
		return (
			<div style={{ backgroundColor: record.get('priorityVO_color'), width: 32, height: 32 }}>{value}</div>
		)
	}

	// header 上的操作按钮

	buttons = [
		'add',
	    <Button key="create" funcType="flat" icon="add" onClick={() => this.ds.create()}>
    	  自定义新增
    	</Button>,
  	];

	// 行上的操作按钮

	commands = ({ record }) => [
		'add',
		// 预置的按钮可以接受第二个参数，用来注入样式
		['delete', { color: 'red }],
		// 当然也可以自己写自定义按钮
		// ds.current 指向的会是你当前点击的行
		<Button 
			key="add" 
			icon="add" 
			onClick={() => console.log(this.ds.current.toData())
			} 
		/>
	];

	render() {
		return (
			<Table dataSet={this.tableDataSet}
				// header 上的按钮
				buttons={this.buttons}
				editMode="inline"
			>
				<Column 
					name="summary" 
					header="概要" 
					renderer={typeRenderer}
					editor
				/>
				<Column 
					name="description"
					header="任务编号" 
					editor
				/>
				<Column name="priorityVO_description" 
					header="优先级" 
				renderer={priorityBackgroundRenderer} 
					editor
				/>
				<Column 
					name="assigneeName" 
					header="经办人" 
					editor
				/>
				<Column 
					name="statusVO_name" 
					header="状态" 
					editor
				/>
				<Column 
					name="reportName" 
					header="报告人" 
					editor
				/>
				<Column 
					// 这里是用来绑操作的列
					command={this.commands}
				/>
			</Table>
		)
	}
}
